import React from "react";
import { Col, Row } from "react-bootstrap";

const Categore=({filterbycategory,allcategory})=>{
    const Onfilter=(cat)=>{
        filterbycategory(cat)
    }
    return(
        <Row className="my-2 mb-5">
            <Col sm="12" className="justify-content-center d-flex">
            all
                <div>
                    <button onClick={()=>Onfilter('الكل')} className="btn mx-2">الكل</button>
                    <button onClick={()=>Onfilter('فطور')} className="btn mx-2">فطور</button>
                    <button onClick={()=>Onfilter('غداء')} className="btn mx-2">غداء</button>
                    <button onClick={()=>Onfilter('عشاء')} className="btn mx-2">عشاء</button>
                </div>
            </Col>
        </Row>
    )
}
export default Categore;
